import React from 'react';
import styled from 'styled-components';

const Section = styled.section`
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  align-items: center;
  text-align: center;
  padding: 2.5rem 0;
  background-size: cover;
  background-position: center;
`;

const Container = styled.div`
  display: flex;
  justify-content: center;
  flex-direction: column-reverse;
  align-items: center;
  gap: 2.5rem;
  margin: 0 1.25rem;
`;

const Heading = styled.h1`
  font-size: 2rem;
  font-weight: 800;
  margin-top: 1.5rem;
  background-image: linear-gradient(to right, cyan, violet);
  color: transparent;
  -webkit-background-clip: text;
  background-clip: text;

  @media (min-width: 1024px) {
    font-size: 4rem;
  }
`;

const SubText = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const Paragraph = styled.p`
  font-size: 1rem;

  @media (min-width: 1024px) {
    font-size: 1.25rem;
  }
`;

const ButtonLink = styled.a`
  width: 100%;
  display: flex;
  justify-content: center;
  text-decoration: none;

  @media (min-width: 768px) {
    width: 50%;
  }
`;

const Button = styled.button`
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 0.5rem;
  width: 100%;
  padding: 0.5rem 1rem;
  font-size: 1.125rem;
  color: white;
  background: black;
  border: 1px solid white;
  border-radius: 9999px;
  transition: background 0.2s, color 0.2s;

  &:hover {
    background: white;
    color: black;
  }
`;

const InputContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  gap: 0.75rem;
`;

const Input = styled.input`
  padding: 0.75rem;
  background: #333;
  color: white;
  border: 1px solid #444;
  border-radius: 4px;
`;

const CopyButton = styled.button`
  padding: 0.75rem 1rem;
  background: #444;
  color: white;
  border: 1px solid #555;
  border-radius: 4px;
  cursor: pointer;

  &:hover {
    background: #555;
  }
`;

const Hero = () => {
  const changeCopyText = (button: HTMLButtonElement) => {
    const copyTextElement = button.querySelector('.copy-text');
    if (copyTextElement) {
      copyTextElement.textContent = 'Copied';
    }
  };

  return (
    <Section>
      <Container>
        <Heading>KENDRICK THE $LLAMA</Heading>
        <SubText>
          <Paragraph>LP Burned.</Paragraph>
          <Paragraph>100% community owned.</Paragraph>
        </SubText>
        <ButtonLink href="https://pancakeswap.finance/swap?outputCurrency=0xbEF1a4b285274B64876f61dB5cCF07E439312D43">
          <Button>BUY</Button>
        </ButtonLink>
        <InputContainer>
          <Input id="hs-clipboard-input" type="text" value="0xbEF1a4b285274B64876f61dB5cCF07E439312D43" readOnly />
          <CopyButton data-clipboard-target="#hs-clipboard-input" onClick={(e) => changeCopyText(e.currentTarget)}>
            <svg
              className="js-clipboard-default"
              xmlns="http://www.w3.org/2000/svg"
              width="24"
              height="24"
              viewBox="0 0 24 24"
              fill="none"
              stroke="currentColor"
              strokeWidth="2"
              strokeLinecap="round"
              strokeLinejoin="round"
            >
              <rect width="8" height="4" x="8" y="2" rx="1" ry="1"></rect>
              <path d="M16 4h2a2 2 0 0 1 2 2v14a2 2 0 0 1-2 2H6a2 2 0 0 1-2-2V6a2 2 0 0 1 2-2h2"></path>
            </svg>
            <span className="copy-text">Copy</span>
          </CopyButton>
        </InputContainer>
      </Container>
    </Section>
  );
};

export default Hero;
